mapper.insert:
INSERT INTO FREE_LIST VALUES(
	seq_free_list.NEXTVAL ,#{seq_free_user}, #{f_title}, #{f_content}, SYSDATE
)

mapper.update:
UPDATE FREE_LIST 
SET 
f_title=#{f_title},
f_content=#{f_content}
WHERE f_list_seq=#{seq_free_list}

mapper.insert:
INSERT INTO FREE_DELETE
VALUE(
	SELECT f_list_seq, f_user_seq, f_title, f_content, f_regdate, SYSDATE FROM FREE_LIST
	WHERE f_list_seq=#{seq_free_list}
)

INSERT INTO FREE_update
VALUE(
	SELECT f_list_seq, f_user_seq, f_title, f_content, f_regdate, SYSDATE FROM FREE_LIST
	WHERE f_list_seq=#{seq_free_list}
)

mapper.select:
SELECT * FROM (SELECT result.*, ROWNUM rnum FROM (SELECT seq_free_list, seq_free_user, f_title, f_content,
FROM free_list
ORDER BY seq_free_list DESC ) result ) WHERE rnum BETWEEN #{startRowNum} AND #{endRowNum}
