mapper.insert:
INSERT INTO FREE_LIST VALUES(
	seq_free_list.NEXTVAL ,#{seq_free_user}, #{f_title}, #{f_content}, SYSDATE
)

mapper.select:
SELECT * FROM (SELECT result.*, ROWNUM rnum FROM (SELECT seq_free_list, seq_free_user, f_title, f_content,
FROM free_list
ORDER BY seq_free_list DESC ) result ) WHERE rnum BETWEEN #{startRowNum} AND #{endRowNum}
